mapper.select:
select distinct h_shop, h_address, h_tel from daegu_hotel_info
where h_address like #{region}
and h_offer like #{offer}
and h_facilities like #{facility};

select distinct h_shop, h_tel, h_address, h_offer, h_facilities, h_tag  from daegu_hotel_info
where h_tag like '%넷플릭스%' or h_tag like '%빔프로젝트%' or
h_tag like '%DVD%';

select distinct h_shop, h_tel, h_address, h_offer, h_facilities, h_tag  from daegu_hotel_info
where h_tag like '%공항%' or h_tag like '%역%' or h_tag like '%정류장%' or
h_tag like '%ic%';

select distinct h_shop, h_tel, h_address, h_offer, h_facilities, h_tag  from daegu_hotel_info
where h_tag like '%길%' or h_tag like '%골목%' or
h_tag like '%거리%' or h_tag like '%로%';
